package api.test;import api.endpoints.UserEnderPoints;import api.payload.User;import api.utilities.DataProviders;import io.restassured.response.Response;import org.testng.Assert;import org.testng.annotations.Test;public class DDTest {    @Test(priority = 1, dataProvider = "Data",dataProviderClass = DataProviders.class)    public void testPostUser(String userID,String userName, String fname, String lname,String userEmail, String pwd,                             String ph)    {        User userPayload = new User();        userPayload.setId(Integer.parseInt(userID));        userPayload.setUsername(userName);        userPayload.setFirstName(fname);        userPayload.setLastName(lname);        userPayload.setEmail(userEmail);        userPayload.setPhone(ph);        userPayload.setPassword(pwd);        Response response = UserEnderPoints.createUser(userPayload);        response.then().log().all();        Assert.assertEquals(response.getStatusCode(),200);    }    @Test(priority = 2, dataProvider = "UserNames", dataProviderClass = DataProviders.class)    public void testGetUser(String userName)    {        Response response = UserEnderPoints.readUser(userName);        response.then().log().all();        Assert.assertEquals(response.getStatusCode(),200);    }    @Test(priority = 3, dataProvider = "UpdatedData", dataProviderClass = DataProviders.class)    public void testUpdateUser(String userID,String userName, String fname, String lname,String userEmail, String pwd,                               String ph)    {        User userPayload = new User();        userPayload.setId(Integer.parseInt(userID));        userPayload.setUsername(userName);        userPayload.setFirstName(fname);        userPayload.setLastName(lname);        userPayload.setEmail(userEmail);        userPayload.setPhone(ph);        userPayload.setPassword(pwd);        Response response = UserEnderPoints.updateUser(userName,userPayload);        response.then().log().all();        Assert.assertEquals(response.getStatusCode(),200);    }    @Test(priority = 4, dataProvider = "UserNames", dataProviderClass = DataProviders.class)    public void testDeleteUser(String userName)    {        Response response = UserEnderPoints.deleteUser(userName);        Assert.assertEquals(response.getStatusCode(),200);    }}