package api.utilities;import org.testng.annotations.DataProvider;import java.io.IOException;public class DataProviders {    @DataProvider(name = "Data")    public Object[][] getAllData() throws IOException {        String path = System.getProperty("user.dir")+"//testdata//UserData.xlsx";        XLUtilities xl = new XLUtilities(path);        int rownum = xl.getRowCount("Sheet1");        int colnum = xl.getCellCount("Sheet1",1);        Object apiData[][] = new Object[rownum][colnum];        for(int i=1;i<=rownum;i++)        {            for(int j=0;j<colnum;j++)            {                apiData[i-1][j]=xl.getCellData("Sheet1",i,j);            }        }        return apiData;    }    @DataProvider(name = "UserNames")    public Object[] getUserNames() throws IOException {        String path = System.getProperty("user.dir")+"//testdata//UserData.xlsx";        XLUtilities xl = new XLUtilities(path);        int rownum = xl.getRowCount("Sheet1");        Object apiData[] = new Object[rownum];        for (int i=1;i<= rownum;i++)        {            apiData[i-1] = xl.getCellData("Sheet1", i, 1);        }        return apiData;    }    @DataProvider(name = "UpdatedData")    public Object[][] getAllUpdatedData() throws IOException {        String path = System.getProperty("user.dir")+"//testdata//UserData.xlsx";        XLUtilities xl = new XLUtilities(path);        int rownum = xl.getRowCount("Sheet2");        int colnum = xl.getCellCount("Sheet2",1);        Object apiData[][] = new Object[rownum][colnum];        for(int i=1;i<=rownum;i++)        {            for(int j=0;j<colnum;j++)            {                apiData[i-1][j]=xl.getCellData("Sheet2",i,j);            }        }        return apiData;    }}